let nums = { char: 0, orac: -1, bron: -1, seal: -1, lius: -1 };

function load_img(script, img, success=true) {
    if (success) {
        nums[script]++;
        img.src = "data/" + chars[nums.char][1] + "/" + script[0] + "_" + nums[script].toString() + ".svg";
        img.parentElement.style.display = null;
    }
    else {
        if (nums[script]) {
            nums[script] = 0;
            img.src = "data/" + chars[nums.char][1] + "/" + script[0] + "_" + nums[script].toString() + ".svg";
        }
        else {
            img.parentElement.style.display = "none";
        }
    }
}

function change_char(num) {
    document.getElementById("char").value = chars[num][1];
    document.getElementById("def").innerText = chars[num][2];

    load_img("orac", document.getElementById("orac"));
    load_img("bron", document.getElementById("bron"));
    load_img("seal", document.getElementById("seal"));
    load_img("lius", document.getElementById("lius"));
    document.getElementById("trad").innerText = chars[num][0];
    document.getElementById("simp").innerText = chars[num][1];
}

document.getElementById("char").onchange = function () {
    chars.forEach(function (item, index) {
        if (document.getElementById("char").value == item[1]) {
            nums.char = index;
            change_char(nums.char);
        }
    });
}

document.getElementById("orac").onclick = function () { load_img(this.id, this); }
document.getElementById("orac").onerror = function () { load_img(this.id, this, false); }

document.getElementById("bron").onclick = function () { load_img(this.id, this); }
document.getElementById("bron").onerror = function () { load_img(this.id, this, false); }

document.getElementById("seal").onclick = function () { load_img(this.id, this); }
document.getElementById("seal").onerror = function () { load_img(this.id, this, false); }

document.getElementById("lius").onclick = function () { load_img(this.id, this); }
document.getElementById("lius").onerror = function () { load_img(this.id, this, false); }

document.getElementById("next").onclick = function () {
    if (nums.char < chars.length) {
        nums.char++;
        nums.orac = nums.bron = nums.seal = nums.lius = -1;
        change_char(nums.char);
    }
}

document.getElementById("before").onclick = function () {
    if (nums.char > 0) {
        nums.char--;
        nums.orac = nums.bron = nums.seal = nums.lius = -1;
        change_char(nums.char);
    }
}

window.onload = function () {
    change_char(nums.char);
};